function submitForm(){
 var validator = $("#myForm").validate({
  rules: {                   
   name: "required",
   surname: "required",
   phone: "required"
     },  
     errorElement: "span" ,                              
     messages: {
      name: " Enter Name",
      surname: " Enter Email",
      phone: "Enter Contact No"
     }
 });
 if(validator.form()){ // validation perform
  $('form#myForm').attr({action: '#'});   
  $('form#myForm').submit();
 }
}